import HeaderComponent from "./components/Header.vue";
import FooterComponent from "./components/Footer.vue";
export default {
  components: {
    HeaderComponent,
    FooterComponent
  },
  data() {
    return {
      theme: this.getInitialTheme() // Set theme based on system or localStorage
    };
  },
  methods: {
    toggleTheme() {
      this.theme = this.theme === "light-theme" ? "dark-theme" : "light-theme";
      localStorage.setItem("theme", this.theme);
      document.documentElement.setAttribute("data-theme", this.theme);
    },
    getInitialTheme() {
      // Check if user has a stored theme preference
      const storedTheme = localStorage.getItem("theme");
      if (storedTheme) {
        return storedTheme;
      }

      // Check system preference
      const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
      return prefersDark ? "dark-theme" : "light-theme";
    },
    handleSystemThemeChange(event) {
      if (!localStorage.getItem("theme")) {
        this.theme = event.matches ? "dark-theme" : "light-theme";
        document.documentElement.setAttribute("data-theme", this.theme);
      }
    }
  },
  created() {
    document.documentElement.setAttribute("data-theme", this.theme);

    // Listen for system theme changes
    window.matchMedia("(prefers-color-scheme: dark)").addEventListener("change", this.handleSystemThemeChange);
  },
  beforeUnmount() {
    // Cleanup event listener
    window.matchMedia("(prefers-color-scheme: dark)").removeEventListener("change", this.handleSystemThemeChange);
  }
};
